function getHumidityScore(humidity) {
    const humWeighting = 0.25;
    const humReference = 40;

    if (humidity >= 38 && humidity <= 42) {
        return humWeighting * 100;
    } else if (humidity < 38) {
        return (humWeighting / humReference) * humidity * 100;
    } else {
        return ((-humWeighting / (100 - humReference)) * humidity) + 0.416666 * 100;
    }
}

// Function to calculate gas score
function getGasScore(gasResistance, gasReference) {
    const gasWeighting = 0.75;
    const gasLowerLimit = 10000;
    const gasUpperLimit = 300000;

    let gasScore = (gasWeighting / (gasUpperLimit - gasLowerLimit)) * gasResistance -
        (gasLowerLimit * (gasWeighting / (gasUpperLimit - gasLowerLimit))) * 100;
    return Math.max(0, Math.min(75, gasScore));
}

